$(document).ready(function(){chrome.storage.local.get(["domains"],function(a){a.domains.forEach(function(b){$(".balcklist-list").append("<li class='w3-display-container'>"+b.domain+" <button class='w3-button w3-transparent w3-display-right remove-blacklist' data-url='"+b.domain+"'>&times;</button></li>")});$(".remove-blacklist").click(function(b){var a=$(this).attr("data-url");b.preventDefault();removeBlacklistItem(a);$(this).parent("li").hide()})})});
function removeBlacklistItem(a){chrome.runtime.sendMessage({message:"toggleBlacklisted",domain:a},function(a){console.log("blacklisted")})};